INSERT INTO `vendor_events` VALUES (1,'Enjoy the finest delicacy of the season: Yangcheng Lake Hairy Crabs','Embrace Autumn with Our Selection of the World Renowned Yangcheng Lake Hairy Crabs. \r\nEnjoy the season with our finest selection of Yangcheng Lake Hairy Crabs:\r\n- Female: Approx. 250g Each: $63++\r\n​- Male: Approx. 300g Each $68++','2025-10-20','2025-12-15','Red Star Restaurant\r\nBlock 54 Chin Swee Rd, #07-23, Singapore 160054',NULL,NULL,0,'','event_images/rs4.png',1,'2025-11-24 11:27:02.483066','2025-11-25 08:20:05.061208',18,NULL,NULL,'https://www.red-star.com.sg/promo','seasonal_promotion'),(2,'Tampines Central 5 Pasar Malam','The final pasar malam of the year will kick off tomorrow 15 Nov 2025, next to Tampines MRT Station!\r\n\r\nThe event will last for a month, consisting of a wide variety of new and popular stalls that will make you be spoilt for choice.','2025-11-15','2025-12-14','20 Tampines Central 1, Singapore 529538',1.35500000,103.94340000,0,'','event_images/pmlm.png',1,'2025-11-28 13:14:09.710205','2025-11-28 15:04:17.564791',32,NULL,NULL,'','food_fair'),(3,'Mayflower Place Pasar Malam','Ang Mo Kio pasar malam is bursting with fun and smiles! ✨ Bring the whole family down for non-stop excitement, yummy treats, and awesome games!? ?✨','2025-11-24','2025-12-23','Blk 162 Ang Mo Kio Avenue 4, Singapore 560162',1.37410000,103.83880000,0,'','event_images/pmlm2.png',1,'2025-11-28 13:14:09.720035','2025-11-28 13:56:01.682250',32,NULL,NULL,'','other'),(4,'Choa Chu Kang Central Pasar Malam','','2025-11-22','2025-12-09','Blk 355 Choa Chu Kang Central, Singapore 680355',1.38450000,103.74570000,0,'','event_images/pmlm1.png',1,'2025-11-28 13:14:09.720035','2025-11-28 13:48:26.472989',32,NULL,NULL,'','other'),(5,'Christmas on a Great Street','From Sat 8 Nov 2025 – Thu 1 Jan 2026, Orchard Road transforms into\r\na festive wonderland with Orchard Road Business Association’s\r\n(ORBA) “Christmas on A Great Street”, presented by Hitachi.\r\n\r\nThis year’s celebration marks SG60 in vibrant style — under a\r\ndazzling tapestry of festoon lights that set the stage for Singapore’s\r\ngreatest Christmas celebration. With a shared spirit of camaraderie,\r\nthe event brings warmth, joy and connection to all who visit.','2025-11-08','2026-01-01','Ngee Ann City Civic Plaza\r\n391A Orchard Road, Singapore 238873',1.29140000,103.83560000,0,'','event_images/img-event-ngee-ann.89f06a6a.jpg',1,'2025-11-28 13:24:45.123780','2025-11-28 15:03:52.506716',33,NULL,NULL,'https://kiztopia.com/christmas-village-2025/','food_fair');
